      audioCtxRef.current = new AudioContext({ sampleRate: SAMPLE_RATE });
      playbackCtxRef.current = new AudioContext({ sampleRate: SAMPLE_RATE });
      nextPlayTimeRef.current = 0;

      // Connect WebSocket
      const ws = new WebSocket(getWsUrl());
